$("a").on("click",function(o){if(""!==this.hash){o.preventDefault();var n=this.hash;$("html, body").animate({scrollTop:$(n).offset().top},800,function(){window.location.hash=n})}}),$(document).ready(function(){var o=$(".header__background");$(window).scroll(function(){o.css("margin-top",$(window).scrollTop()/3),o.css("opacity",1-$(window).scrollTop()/o.height())})});