var selectBtn=document.getElementsByClassName("dropdown"),dropdownMenu=document.getElementsByClassName("dropdownMenu");for(i=0;i<selectBtn.length;i++)selectBtn[i].onclick=function(){if(-1<this.className.indexOf("active"))for(j=0;j<selectBtn.length;j++)removeClass(selectBtn[j],"active");else addClass(this,"active")};for(i=0;i<dropdownMenu.length;i++){var child=dropdownMenu[i].children;for(j=0;j<child.length;j++)child[j].onclick=function(){var e=this.innerHTML;this.parentNode.previousElementSibling.children[0].innerHTML=e,toggleClass(this.parentNode,"showMenu")}}function toggleClass(e,n){var s=e.className;-1<s.indexOf(n)?e.className=s.replace(" "+n,""):e.className=s+" "+n}function addClass(e,n){var s=e.className;s.indexOf(n)<1&&(e.className=s+" "+n)}function removeClass(e,n){var s=e.className;-1<s.indexOf(n)&&(e.className=s.replace(" "+n,""))}window.addEventListener("click",function(e){for(i=0;i<selectBtn.length;i++)e.target!=selectBtn[i].children[0]&&removeClass(selectBtn[i],"active")});